import React from 'react';
import Carousel from "../../components/carousel/index.js"
import PropTypes from 'prop-types'
import {CardAvatar} from '../CardAvatar/CardAvatar.js';
import { SingleCard } from "../../components/HomePage/styles";
import './styles/index.scss';

export default class CardsCarusel extends React.Component {
    CardAvatar = React.createRef();

    render() {
        const { cards, handleSelectCard } = this.props;
        return (
            <>
              {cards.length === 1 && (<SingleCard className="single-card">
                <CardAvatar imageUrl={cards[0].get('imageLogoUrl')} onClick={this.handleStoresBtnClick} />
              </SingleCard>)}
              {cards.length > 1 && (<Carousel
                 cellSpacing={10}
                 slideWidth={0.8}
                 slideHeight={200}
                 disableAnimation
                 infinite
                 selectedIndex={1}
                 swiping
                 beforeChange={(from, to) => console.log(`slide from ${from} to ${to}`)}
                 afterChange={index => handleSelectCard(index)}

               >
                 {cards.map((cardMapped, index) => {
                     const cardAsJson = cardMapped.toJSON();

                    return <CardAvatar ref={this.CardAvatar} key={cardMapped.id} imageUrl={cardAsJson.imageLogoUrl} />
                 })}
            </Carousel>)}
            </>
        );
    }
}

CardsCarusel.propTypes = {
    cards: PropTypes.array.isRequired,
    handleSelectCard: PropTypes.func.isRequired,
}
