import React, { useState } from "react";
import MenuBar from '../MenuBar/Menubar.js'
import closeIcon from "../../assets/icons/closeIcon.svg";
import { IconContainer, Icon, Button, ButtonContent, Title } from "../ForgotPassword/style/index.js";
import { PasswordInput, ButtonContainer, BottomContainer, TitleContainer, InputContainer, SecondInput, ToggleIcon, IconButtonContainer } from "./style";
import { RecoveryPopup } from "../RecoveryPopup/RecoveryPopup.js";
import eyeShowIcon from '../../assets/icons/eyeShowIcon.svg';
import eyeHideIcon from '../../assets/icons/eyeHideIcon.svg';
import { CommonContainerView } from "../common/commonContainer.js";

export const PassRecovery = ({ history }) => {
    const [showPopup, setShowPopup] = useState(false);

    const [showPassword, setShowPassword] = useState(true);

    const togglePopup = () => {
        setShowPopup(!showPopup);
    }

    const recoveryHandle = () => {
        history.push('/home/params');
    }

    return (
        <>
            {showPopup && <RecoveryPopup active title={'Success'} subtitle={'Your new password has been saved.'} buttonTitle={'Login'} recoveryHandle={recoveryHandle} />}
            <CommonContainerView>
                <IconContainer onClick={() => history.push('/home')}>
                    <Icon src={closeIcon} />
                </IconContainer>
                <TitleContainer>
                    <Title>Enter you password</Title>
                </TitleContainer>
                <BottomContainer>
                    <PasswordInput placeholder="1234567"/>
                    <InputContainer>
                        <SecondInput
                        type={showPassword ? "text" : "password"}
                        placeholder="2134567"/>
                    {showPassword 
                    ?
                    <IconButtonContainer onClick={() => setShowPassword(!showPassword)}>
                    <ToggleIcon src={eyeHideIcon} />
                    </IconButtonContainer>
                    :
                    <IconButtonContainer onClick={() => setShowPassword(!showPassword)}>
                    <ToggleIcon src={eyeShowIcon} />
                    </IconButtonContainer>
                    }   
                    </InputContainer>
                    <ButtonContainer onClick={togglePopup}>
                        <Button>
                            <ButtonContent>
                                Save New Password
                            </ButtonContent>
                        </Button>
                    </ButtonContainer>
                </BottomContainer>
            </CommonContainerView>
            <MenuBar/>
        </>
    );
}
