import React from "react";
import {Icon} from "../RegisterPopup/style"
import {Content, IconContainer, InstallationPopupContainer, LinkButtonContent} from "./style"
import closeIcon from "../../assets/icons/closeIcon.svg";
import shortcutIcon from "../../assets/icons/shortcutIconBlack.svg";
import shortcutIconAndroid from "../../assets/icons/shortcutIconAndroidBlack.svg";
import '../popup/index.scss'
import {PopupViewContainer} from "../popup/style";
import {FormattedMessage, injectIntl} from 'react-intl';

const InstallationPopup = ({contentText, active, buttonContent, closeModalHandler, installAppHandler }) => {
    const isAndroid = /android/i.test(navigator.userAgent);
    return (
        <PopupViewContainer className={active ? 'active' : null}>
            <InstallationPopupContainer>
                <IconContainer onClick={closeModalHandler}>
                    <Icon src={closeIcon} />
                </IconContainer>
                <Content>
                 {contentText} <FormattedMessage id={"home.addToHomePopUpMessge1"} />                
                </Content>
                <LinkButtonContent onClick={installAppHandler}>
                    {buttonContent} <FormattedMessage id={"home.addToHomePopUpMessge2"} />
                    <Icon src={isAndroid ? shortcutIconAndroid : shortcutIcon} />
                    <FormattedMessage id={"home.addToHomePopUpMessge3"} />
                </LinkButtonContent>
            </InstallationPopupContainer>
        </PopupViewContainer>
    )
}

export default injectIntl(InstallationPopup)
