import React from 'react';
import {Redirect} from 'react-router-dom';
import {SplashScreenContent, SplashScreenContainer, Image} from './styles';
import SplashScreenLogo from '../../assets/images/SplashScreen/splashScreenLogo.svg'

export const SplashScreen = class SplashScreen extends React.Component {
    SPLASH_SCREEN_TIME = 2000;

    state = {
        redirectToHome: false,
    }

    componentDidMount() {
        setTimeout(() => {
            this.setState({redirectToHome: true});
        }, this.SPLASH_SCREEN_TIME)
    }

    render() {
        if (this.state.redirectToHome) {
            return <Redirect to={'/home'} />
        }

        return (
            <SplashScreenContainer className="view">
                <SplashScreenContent className="uvcontent">
                        <Image src={SplashScreenLogo} className="splashlogo"/>
                </SplashScreenContent>
            </SplashScreenContainer>
        );
    }
}
