import React, { Component } from "react";
import { Redirect } from "react-router-dom";
import IdleTimer from "react-idle-timer";

import { isManifestSetted, setClientManifest } from "../../utils/storage";
import Notifications from "../Notifications";
import Menubar from "../MenuBar/Menubar";
// import IntroSlider from "react-intro-slider";
import {
  Icon,
  ShortcutContainer,
  ShortcutContent,
  HomeContainer,
  BackgroundCircle,
  StampCardDetails,
  CardsContainer
} from "./styles";
import "./styles/index.scss";
import shortcutIcon from "../../assets/icons/shortcutIcon.svg";
import shortcutIconAndroid from "../../assets/icons/shortcutIconAndroid.svg";
import { User } from "parse";
// import { Theme } from "../Theme";
import GlobalRequireAuth from "../../pages/SettingsPage/GlobalRequireAuth";
import InstallationPopup from "../InstallationPopup/InstallationPopup";
import { FormattedMessage, injectIntl } from "react-intl";
import FinalSuccessPopUp from "../../components/GiftScan/FinalSuccessPopUp";

const UserParse = User.extend("User");

class HomePage extends Component {
  state = {
    clicked: false,
    sliderIsOpen: false,
    InstallationPopup: false,
    finalModalIsOpen: false,
    timeout: 3000,
    showModal: false,
    isTimedOut: false
  };

  constructor(props) {
    super(props);

    // Just for mock data
    let currentUser = User.current();
    this.idleTimer = null;

    if (currentUser) {
      this.props
        .loadLoyaltyCards(currentUser)
        .then(lastIndex => this.props.selectAndChangeCardId(lastIndex));
    }
  }

  componentDidMount = () => {
    const { fakeId } = this.props.match.params;
    // let localToken = undefined;

    if (UserParse.current() && !this.props.user) {
      this.props.receiveUserLogIn(UserParse.current().toJSON());
    }

    if (fakeId) {
      this.props.loadLoyaltyFakeCard(fakeId);
    } else {
      if (!isManifestSetted()) {
        this.setState({ sliderIsOpen: true });
      }
      // alert('beforeCardId')

      // getCardId().then(cardId => {
      //   // alert('afterCardId :' +cardId)
      //   localToken = token || cardId;
      //   if (token && !cardId) {
      //     // alert('here1:'+token)
      //     console.log("Listening for Install prompt");
      //     this.props.fetchInitLoyaltyCard(token);
      //   } else if (token && cardId) {
      //     // alert('here3:'+cardId)
      //     this.props.loadLoyaltyCard(cardId);
      //   } else if (cardId) {
      //     // alert('here3:' + localToken)
      //     this.props.loadLoyaltyCard(localToken);
      //   }
      // });
    }
    if (window) {
      window.addEventListener("beforeinstallprompt", e => {
        // For older browsers
        e.preventDefault();
        console.log("Install Prompt fired");
        this.installPrompt = e;
        // See if the app is already installed, in that case, do nothing
        if (
          (window.matchMedia &&
            window.matchMedia("(display-mode: standalone)").matches) ||
          window.navigator.standalone === true
        ) {
          return false;
        }
        // Set the state variable to make button visible
        this.setState({ installButton: true });
      });
      // window.addEventListener("online", () => {
      //   notify.hide();
      //   this.loadLoyaltyCard("");
      // });
      // window.addEventListener("offline", () => {
      //   notify.show("offlinemode");
      // });
    }
  };

  render() {
    const { error } = this.props;
    if (error) {
      return <Redirect to="/404" />;
    }
    return (
      <>
        <GlobalRequireAuth {...this.props}>
          <HomeContainer>
            <Notifications />
          </HomeContainer>
          <Menubar />
        </GlobalRequireAuth>
      </>
    );
  }
}
export default injectIntl(HomePage);
