    flex-direction: column;
    justify-content: center;
    align-items: center;
    box-shadow: 0px 2px 16px rgba(50, 146, 255, 0.15);
    border-radius: 12px;
`
export const Content = styled.div`
    font-family: 'Avenir Next';
    font-size: 14px;
    line-height: 140%;
    text-align: center;
    color: rgba(0, 0, 0, 0.6);
    width: 70%;

    @media (max-width: ${iphone5SEMaxWidth}px) {
        font-size: 12px;
        line-height: 120%;
    }
`
export const LinkButtonContent = styled(Content)`
    color: rgba(0, 0, 0, 0.9);
    margin-top: 6%;
    display: flex;
    justify-content: center;
    align-items: center;
    flex-wrap: wrap;

    img {
        height: 25px;
        width: 20px;
        display: inline-block;
        margin: 0 5px;
    }

    @media (max-width: ${iphone6MaxWidth}px) {
        img {
            height: 20px;
            width: 15px;
        }
    }
`
export const IconContainer = styled.div`
    display: flex;
    position: absolute;
