import React, { Component } from "react";
import { Redirect } from "react-router-dom";
import IdleTimer from "react-idle-timer";

import { isManifestSetted, setClientManifest } from "../../utils/storage";
import Notifications from "../Notifications";
import Menubar from "../MenuBar/Menubar";
// import IntroSlider from "react-intro-slider";
import {
  Icon,
  ShortcutContainer,
  ShortcutContent,
  HomeContainer,
  BackgroundCircle,
  StampCardDetails,
  CardsContainer
} from "./styles";
import "./styles/index.scss";
import shortcutIcon from "../../assets/icons/shortcutIcon.svg";
import shortcutIconAndroid from "../../assets/icons/shortcutIconAndroid.svg";
import { User } from "parse";
// import { Theme } from "../Theme";
import GlobalRequireAuth from "../../pages/SettingsPage/GlobalRequireAuth";
import InstallationPopup from "../InstallationPopup/InstallationPopup";
import { FormattedMessage, injectIntl } from "react-intl";
import FinalSuccessPopUp from "../../components/GiftScan/FinalSuccessPopUp";

const UserParse = User.extend("User");

class HomePage extends Component {
  state = {
    clicked: false,
    sliderIsOpen: false,
    InstallationPopup: false,
    finalModalIsOpen: false,
    timeout: 3000,
    showModal: false,
    isTimedOut: false
  };

  constructor(props) {
    super(props);

    let currentUser = User.current();

    if (currentUser) {
      this.props
        .loadLoyaltyCards(currentUser)
        .then(lastIndex => this.props.selectAndChangeCardId(lastIndex));
    }
  }

  componentDidMount = () => {
  };

  render() {
    const { error } = this.props;
    if (error) {
      return <Redirect to="/404" />;
    }
    return (
      <>
        <GlobalRequireAuth {...this.props}>
          <HomeContainer>
            <Notifications />
          </HomeContainer>
          <Menubar />
        </GlobalRequireAuth>
      </>
    );
  }
}
export default injectIntl(HomePage);
