import styled from 'styled-components'

export const NotificationsWrapper = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  padding: 15px;
  margin-bottom: 20px;
  border-radius: 0;
  background-color: #e9d186;
  border-color: #e9d186;
  z-index: 1000;

  .message-text {
    font-size: 18px;
    color: #051039;
  }
`
